  --shadow-xl: 0px 2px 0px 0px hsl(221 83% 53% / 0.00), 0px 8px 10px -1px hsl(221 83% 53% / 0.00);
  --shadow-2xl: 0px 2px 0px 0px hsl(221 83% 53% / 0.00);
  --tracking-normal: 0em;
  --spacing: 0.25rem;
}

.dark {
  --background: hsl(222 15% 8%);
  --foreground: hsl(210 40% 98%);
  --card: hsl(222 15% 11%);
  --card-foreground: hsl(210 40% 98%);
  --popover: hsl(222 15% 11%);
  --popover-foreground: hsl(210 40% 98%);
  --primary: hsl(221 83% 53%);
  --primary-foreground: hsl(0 0% 98%);
  --secondary: hsl(222 15% 15%);
  --secondary-foreground: hsl(210 40% 98%);
  --muted: hsl(222 15% 15%);
  --muted-foreground: hsl(215 16% 57%);
  --accent: hsl(262 83% 58%);
  --accent-foreground: hsl(0 0% 98%);
  --destructive: hsl(0 84% 60%);
  --destructive-foreground: hsl(0 0% 98%);
  --border: hsl(222 15% 20%);
  --input: hsl(222 15% 20%);
  --ring: hsl(221 83% 53%);
  --chart-1: hsl(221 83% 53%);
  --chart-2: hsl(159 100% 36%);
  --chart-3: hsl(42 93% 56%);
  --chart-4: hsl(147 79% 42%);
  --chart-5: hsl(341 75% 51%);
  --sidebar: hsl(222 15% 11%);
  --sidebar-foreground: hsl(210 40% 98%);
  --sidebar-primary: hsl(221 83% 53%);
  --sidebar-primary-foreground: hsl(0 0% 98%);
  --sidebar-accent: hsl(262 83% 58%);
  --sidebar-accent-foreground: hsl(0 0% 98%);
  --sidebar-border: hsl(222 15% 20%);
  --sidebar-ring: hsl(221 83% 53%);
  --font-sans: Inter, system-ui, sans-serif;
  --font-serif: Georgia, serif;
  --font-mono: Menlo, monospace;
  --radius: 12px;
  --shadow-2xs: 0px 2px 0px 0px hsl(221 83% 53% / 0.00);
  --shadow-xs: 0px 2px 0px 0px hsl(221 83% 53% / 0.00);
  --shadow-sm: 0px 2px 0px 0px hsl(221 83% 53% / 0.00), 0px 1px 2px -1px hsl(221 83% 53% / 0.00);
  --shadow: 0px 2px 0px 0px hsl(221 83% 53% / 0.00), 0px 1px 2px -1px hsl(221 83% 53% / 0.00);
  --shadow-md: 0px 2px 0px 0px hsl(221 83% 53% / 0.00), 0px 2px 4px -1px hsl(221 83% 53% / 0.00);
  --shadow-lg: 0px 2px 0px 0px hsl(221 83% 53% / 0.00), 0px 4px 6px -1px hsl(221 83% 53% / 0.00);
  --shadow-xl: 0px 2px 0px 0px hsl(221 83% 53% / 0.00), 0px 8px 10px -1px hsl(221 83% 53% / 0.00);
  --shadow-2xl: 0px 2px 0px 0px hsl(221 83% 53% / 0.00);
}

/* Custom gradient utilities */
.gradient-bg {
  background: linear-gradient(135deg, hsl(var(--primary)) 0%, hsl(var(--secondary)) 100%);
}

/* Animation utilities */
.typing-indicator {
  animation: typing 1.5s infinite;
}

@keyframes typing {
  0%, 60%, 100% { 
    opacity: 0.3; 
  }
  30% { 
    opacity: 1; 
  }
}

.pulse-ring {
  animation: pulse-ring 2s infinite;
}

@keyframes pulse-ring {
  0% { 
    transform: scale(1); 
    opacity: 1; 
  }
  100% { 
    transform: scale(1.2); 
    opacity: 0; 
  }
}

.slide-up {
  animation: slideUp 0.3s ease-out;
}

@keyframes slideUp {
  from { 
    transform: translateY(100%); 
    opacity: 0; 
  }
  to { 
    transform: translateY(0); 
    opacity: 1; 
  }
}

/* Content card utilities */
.line-clamp-2 {
  display: -webkit-box;
  -webkit-line-clamp: 2;
  -webkit-box-orient: vertical;
  overflow: hidden;
}

@layer base {
  * {
    @apply border-border;
  }

  body {
    @apply font-sans antialiased bg-background text-foreground;
  }

  /* Custom scrollbar */
  ::-webkit-scrollbar {
    width: 6px;
  }

  ::-webkit-scrollbar-track {
    @apply bg-muted;
  }

  ::-webkit-scrollbar-thumb {
    @apply bg-muted-foreground rounded-full;
  }

  ::-webkit-scrollbar-thumb:hover {
    @apply bg-primary;
  }
}

/* Component-specific styles */
.prose {
  @apply text-foreground;
}

.prose h1,
.prose h2,
.prose h3,
.prose h4,
.prose h5,
.prose h6 {
  @apply text-foreground;
}

.prose p {
  @apply text-foreground;
}

.prose code {
  @apply bg-muted text-foreground px-1 py-0.5 rounded;
}

.prose pre {
  @apply bg-muted text-foreground;
}

.prose blockquote {
  @apply border-l-primary text-foreground;
}

.prose a {
  @apply text-primary hover:opacity-80;
}

/* Dark theme prose adjustments */
.dark .prose {
  @apply text-foreground;
}

.dark .prose h1,
.dark .prose h2,
.dark .prose h3,
.dark .prose h4,
.dark .prose h5,
.dark .prose h6 {
  @apply text-foreground;
}

.dark .prose p {
  @apply text-foreground;
}

.dark .prose code {
  @apply bg-muted text-foreground;
}

.dark .prose pre {
  @apply bg-muted text-foreground;
}

.dark .prose blockquote {
  @apply border-l-primary text-foreground;
}

