import { ExternalLink, Calendar, MapPin, DollarSign } from "lucide-react";
import { Card, CardContent } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";

interface ContentCardProps {
  content: {
    title?: string;
    description?: string;
    image?: string;
    price?: string;
    location?: string;
    category?: string;
    date?: string;
    url?: string;
  };
}

export function ContentCard({ content }: ContentCardProps) {
  const {
    title = "Untitled Content",
    description = "No description available",
    image,
    price,
    location,
    category,
    date,
    url
  } = content;

  return (
    <Card className="overflow-hidden border shadow-sm hover:shadow-md transition-shadow" data-testid={`content-card-${title.toLowerCase().replace(/\s+/g, '-')}`}>
      {image && (
        <img 
          src={image} 
          alt={title}
          className="w-full h-24 object-cover"
          loading="lazy"
        />
      )}
      <CardContent className="p-3">
        <div className="space-y-2">
          <div className="flex items-start justify-between">
            <h5 className="font-medium text-foreground text-sm line-clamp-2">{title}</h5>
            {category && (
              <Badge variant="secondary" className="ml-2 text-xs">{category}</Badge>
            )}
          </div>

          <p className="text-xs text-muted-foreground line-clamp-2">{description}</p>

          <div className="flex items-center justify-between text-xs text-muted-foreground">
            <div className="flex items-center space-x-3">
              {location && (
                <span className="flex items-center" data-testid="content-location">
                  <MapPin className="h-3 w-3 mr-1" />
                  {location}
                </span>
              )}
              {date && (
                <span className="flex items-center" data-testid="content-date">
                  <Calendar className="h-3 w-3 mr-1" />
                  {date}
                </span>
              )}
            </div>

            {price && (
              <span className="flex items-center font-medium text-primary" data-testid="content-price">
                <DollarSign className="h-3 w-3 mr-1" />
                {price}
              </span>
            )}
          </div>

          {url && (
            <Button 
              variant="outline" 
              size="sm" 
              className="w-full text-xs"
              onClick={() => window.open(url, '_blank')}
              data-testid="button-view-details"
            >
              View Details <ExternalLink className="ml-1 h-3 w-3" />
            </Button>
          )}
        </div>
      </CardContent>
    </Card>
  );
}

client/src/components/InputBar.tsx.content:
import { useState, useRef } from "react";
import { Send, Mic, MicOff, Paperclip, Download, Trash2 } from "lucide-react";
import { motion } from "framer-motion";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";

interface InputBarProps {
  onSendMessage: (message: string) => void;
  isLoading: boolean;
  isRecording: boolean;
  onStartRecording: () => void;
  onExportHistory: () => void;
  onClearHistory: () => void;
}

export function InputBar({
  onSendMessage,
  isLoading,
  isRecording,
  onStartRecording,
  onExportHistory,
  onClearHistory
}: InputBarProps) {
  const [input, setInput] = useState("");
  const [showActions, setShowActions] = useState(false);
  const inputRef = useRef<HTMLInputElement>(null);

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    if (input.trim() && !isLoading) {
      onSendMessage(input.trim());
      setInput("");
    }
  };

  const quickActions = [
    { icon: Download, label: "Export Chat", action: onExportHistory },
    { icon: Trash2, label: "Clear History", action: onClearHistory }
  ];

  return (
    <div className="border-t bg-card p-4" data-testid="input-bar">
      {/* Quick Actions */}
      {showActions && (
        <motion.div
          initial={{ height: 0, opacity: 0 }}
          animate={{ height: "auto", opacity: 1 }}
          className="flex gap-2 mb-3 pb-3 border-b"
        >
          {quickActions.map((action, idx) => (
            <Button
              key={idx}
              variant="ghost"
              size="sm"
              onClick={action.action}
              className="text-xs"
              data-testid={`button-${action.label.toLowerCase().replace(' ', '-')}`}
            >
              <action.icon className="h-3 w-3 mr-1" />
              {action.label}
            </Button>
          ))}
        </motion.div>
      )}

      {/* Input Form */}
      <form onSubmit={handleSubmit} className="flex gap-2">
        <div className="flex-1 relative">
          <Input
            ref={inputRef}
            type="text"
            value={input}
            onChange={(e) => setInput(e.target.value)}
            placeholder="Ask about your content..."
            disabled={isLoading}
            className="pr-10"
            data-testid="input-message"
          />
          <Button
            type="button"
            variant="ghost"
            size="icon"
            onClick={() => setShowActions(!showActions)}
            className="absolute right-1 top-1/2 transform -translate-y-1/2 h-8 w-8"
            data-testid="button-actions"
          >
            <Paperclip className="h-4 w-4" />
          </Button>
        </div>

        {/* Voice Button */}
        <Button
          type="button"
          variant={isRecording ? "destructive" : "outline"}
          size="icon"
          onClick={onStartRecording}
          disabled={isLoading}
          className={isRecording ? "animate-pulse" : ""}
          data-testid="button-voice"
        >
          {isRecording ? <MicOff className="h-4 w-4" /> : <Mic className="h-4 w-4" />}
        </Button>

        {/* Send Button */}
        <Button
          type="submit"
          disabled={isLoading || !input.trim()}
          className="bg-gradient-to-r from-primary to-secondary"
          data-testid="button-send"
        >
          <Send className="h-4 w-4" />
        </Button>
      </form>
    </div>
  );
}

client/src/components/MessageList.tsx.content:
import { motion, AnimatePresence } from "framer-motion";
import { Bot, User, Clock, Sparkles } from "lucide-react";
import ReactMarkdown from "react-markdown";
import { ContentCard } from "./ContentCard";
import { Button } from "@/components/ui/button";

interface Message {
  id: number;
  role: 'user' | 'assistant';
  content: string;
  timestamp: string;
  isStreaming?: boolean;
  responseTime?: number;
  contentReferences?: any[];
}

interface MessageListProps {
  messages: Message[];
  isTyping: boolean;
  messagesEndRef: React.RefObject<HTMLDivElement>;
}

export function MessageList({ messages, isTyping, messagesEndRef }: MessageListProps) {
  const handleSuggestionClick = (text: string) => {
    window.dispatchEvent(new CustomEvent('contentiq:message', {
      detail: { message: text }
    }));
  };

  return (
    <div className="flex-1 overflow-y-auto p-4 space-y-4 bg-gradient-to-b from-muted/30 to-background" data-testid="message-list">
      {messages.length === 0 && (
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          className="text-center py-12"
        >
          <Bot className="mx-auto text-muted-foreground mb-4 h-12 w-12" />
          <h3 className="text-lg font-semibold text-foreground mb-2">
            Welcome to ContentIQ!
          </h3>
          <p className="text-muted-foreground text-sm max-w-xs mx-auto mb-6">
            Ask me anything about your content. I can help you find information, identify gaps,
            and suggest improvements.
          </p>
          <div className="space-y-2">
            <SuggestionChip 
              text="Show me available tours" 
              onClick={() => handleSuggestionClick("Show me available tours")}
            />
            <SuggestionChip 
              text="What content is missing?" 
              onClick={() => handleSuggestionClick("What content is missing?")}
            />
            <SuggestionChip 
              text="Analytics dashboard" 
              onClick={() => handleSuggestionClick("Show me the analytics dashboard")}
            />
          </div>
        </motion.div>
      )}

      <AnimatePresence>
        {messages.map((message, index) => (
          <motion.div
            key={message.id}
            initial={{ opacity: 0, x: message.role === 'user' ? 20 : -20 }}
            animate={{ opacity: 1, x: 0 }}
            transition={{ delay: index * 0.05 }}
            className={`flex ${message.role === 'user' ? 'justify-end' : 'justify-start'}`}
            data-testid={`message-${message.role}-${message.id}`}
          >
            <div className={`flex gap-3 max-w-[80%] ${message.role === 'user' ? 'flex-row-reverse' : ''}`}>
              <div className={`flex-shrink-0 w-8 h-8 rounded-full flex items-center justify-center ${
                message.role === 'user' 
                  ? 'bg-primary text-primary-foreground' 
                  : 'bg-gradient-to-r from-secondary to-primary text-primary-foreground'
              }`}>
                {message.role === 'user' ? <User className="h-4 w-4" /> : <Bot className="h-4 w-4" />}
              </div>
              <div className={`flex flex-col gap-1 ${message.role === 'user' ? 'items-end' : 'items-start'}`}>
                <div className={`px-4 py-2 rounded-2xl ${
                  message.role === 'user'
                    ? 'bg-primary text-primary-foreground'
                    : 'bg-card border shadow-sm text-foreground'
                }`}>
                  {message.isStreaming && (
                    <Sparkles className="inline-block mr-1 h-3 w-3 animate-pulse" />
                  )}
                  <ReactMarkdown 
                    components={{
                      p: ({ children }) => <p className="prose prose-sm max-w-none dark:prose-invert">{children}</p>,
                      div: ({ children }) => <div className="prose prose-sm max-w-none dark:prose-invert">{children}</div>
                    }}
                  >
                    {message.content}
                  </ReactMarkdown>
                </div>
                {message.responseTime && (
                  <span className="text-xs text-muted-foreground flex items-center gap-1 px-2">
                    <Clock className="h-3 w-3" />
                    {message.responseTime}ms
                  </span>
                )}
                {message.contentReferences && message.contentReferences.length > 0 && (
                  <div className="mt-2 space-y-2">
                    {message.contentReferences.map((ref, idx) => (
                      <ContentCard key={idx} content={ref} />
                    ))}
                  </div>
                )}
              </div>
            </div>
          </motion.div>
        ))}
      </AnimatePresence>

      {isTyping && (
        <motion.div
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          className="flex items-center gap-2 text-muted-foreground"
          data-testid="typing-indicator"
        >
          <Bot className="h-5 w-5" />
          <div className="flex gap-1">
            <span className="w-2 h-2 bg-muted-foreground rounded-full animate-bounce" style={{ animationDelay: '0ms' }} />
            <span className="w-2 h-2 bg-muted-foreground rounded-full animate-bounce" style={{ animationDelay: '150ms' }} />
            <span className="w-2 h-2 bg-muted-foreground rounded-full animate-bounce" style={{ animationDelay: '300ms' }} />
          </div>
        </motion.div>
      )}
      <div ref={messagesEndRef} />
    </div>
  );
}

interface SuggestionChipProps {
  text: string;
  onClick: () => void;
}

function SuggestionChip({ text, onClick }: SuggestionChipProps) {
  return (
    <Button
      variant="outline"
      size="sm"
      onClick={onClick}
      className="text-xs"
      data-testid={`suggestion-${text.toLowerCase().replace(/\s+/g, '-')}`}
    >
      {text}
    </Button>
  );
}

client/src/components/ThemeProvider.tsx.content:
import { createContext, useContext, useEffect, useState } from "react";

type Theme = "light" | "dark";

interface ThemeContextType {
  theme: Theme;
  toggleTheme: () => void;
}

const ThemeContext = createContext<ThemeContextType | null>(null);

export function ThemeProvider({ children }: { children: React.ReactNode }) {
  const [theme, setTheme] = useState<Theme>("light");

  useEffect(() => {
    // Check localStorage for saved theme
    const savedTheme = localStorage.getItem("contentiq-theme") as Theme;
    if (savedTheme) {
      setTheme(savedTheme);
    } else {
      // Check system preference
      const prefersDark = window.matchMedia("(prefers-color-scheme: dark)").matches;
      setTheme(prefersDark ? "dark" : "light");
    }
  }, []);

  useEffect(() => {
    // Update document class and localStorage
    document.documentElement.classList.remove("light", "dark");
    document.documentElement.classList.add(theme);
    localStorage.setItem("contentiq-theme", theme);
  }, [theme]);

  const toggleTheme = () => {
    setTheme(theme === "light" ? "dark" : "light");
  };

  return (
    <ThemeContext.Provider value={{ theme, toggleTheme }}>
      {children}
    </ThemeContext.Provider>
  );
}

export function useTheme() {
  const context = useContext(ThemeContext);
  if (!context) {
    throw new Error("useTheme must be used within a ThemeProvider");
  }
  return context;
}

